package Loop_Study;

public class For_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//i want to print odd number
		
		for(int i=1;i<=30;i=i+2)
		{
			System.out.println(i);
		}
	
		int y=1;
		while(y<=30)
		{
			System.out.println(y);
			y=y+2;
		}
		int a=1;
		do
		{
			System.out.println(a);
			a=a+1;
		}
		while(a<=30);
	}
	

}
